import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import { IShopContext, ShopContext } from "../context/shop-context";

export const Navbar = () => {
  const { availableMoney, isAuthenticated, setIsAuthenticated } =useContext<IShopContext>(ShopContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    setIsAuthenticated(false); // Logout utilizatorul
    navigate("/"); // Navighează la pagina principală după logout
  };

  const handleLogin = () => {
    if (!isAuthenticated) {
      navigate("/auth"); // Navighează la pagina de autentificare
    }
  };

  return (
    <div className="navbar">
      <div className="navbarTitle">
        <h1>iTech</h1>
      </div>
      <div className="navbarLinks">
        {!isAuthenticated ? (
          <button onClick={handleLogin}>Login</button> // Buton pentru login
        ) : (
          <>
            <Link to="/shop">Shop</Link>
            <Link to="/purchased-items">Purchases</Link>
            <Link to="/checkout">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Link>
            <button onClick={handleLogout}>Logout</button>
            <span> ${availableMoney.toFixed(2)} </span>
          </>
        )}
      </div>
    </div>
  );
};

export default Navbar;
