import React, { useContext, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./styles.css";
import { useGetProducts } from "../../hooks/useGetProducts";
import { IProduct } from "../../models/interfaces";

export const CheckoutPage = () => {
  const { getCartItemCount, getTotalCartAmount, checkout } = useContext(ShopContext);
  const totalAmount = getTotalCartAmount();
  const { products } = useGetProducts();
  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleCheckout = async () => {
    try {
      const result = await checkout(localStorage.getItem("userID"));
      if (result.error) {
        setError(result.error);
        alert(result.errorMessage); // Show error message to user
        return; // Stop the checkout process
      }
      const purchasedProducts = products
        .filter(product => getCartItemCount(product._id) !== 0)
        .map(product => ({
          _id: product._id,
          name: product.productName, 
          price: product.price,
          quantity: getCartItemCount(product._id),
          imageURL: product.imageURL
        }));
      const randomShippingFee = 10;

      navigate("/confirmation", {
        state: {
          products: purchasedProducts,
          totalAmount,
          shippingFee: randomShippingFee
        }
      });
    } catch (err) {
      console.error("Checkout failed:", err);
      setError("An error occurred during checkout.");
    }
  };

  return (
    <div className="cart">
      <div>
        <h1>Your Cart Items</h1>
      </div>
      <div className="cart">
        {products.map((product: IProduct) => {
          if (getCartItemCount(product._id) !== 0) {
            return <CartItem key={product._id} data={product} />;
          }
        })}
      </div>
      {totalAmount > 0 ? (
        <div className="checkout">
          <p>Subtotal: ${totalAmount.toFixed(2)}</p>
          <button onClick={() => navigate("/")}>Continue Shopping</button>
          <button onClick={handleCheckout}>Checkout</button>
        </div>
      ) : (
        <h1>Your Shopping Cart is Empty</h1>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
};
