import React, { useState } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";
import "./styles.css";


const CustomArrow = ({ direction, onClick }) => {
  return (
    <div
      className={`arrow arrow-${direction}`}
      onClick={onClick}
      style={{
        width: "30px",
        height: "30px",
        border: "solid black",
        borderWidth: "0 2px 2px 0",
        display: "inline-block",
        padding: "3px",
        borderRadius: "3px",
        transform: `rotate(${direction === "left" ? 135 : -45}deg)`, // Rotim săgeata în funcție de direcția specificată
      }}
    ></div>
  );
};


const Home = () => {

  const images = ["/nfc.jpg", "/n.jpg", "/nn.jpg"];
  const [currentIndex, setCurrentIndex] = useState(0);
  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };
  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };





  return (
    <div>
       <div className="image-container">
      <img src={images[currentIndex]} alt="Slide" className="image" />

      {/* Săgeată stânga */}
      <div className="arrow left" onClick={prevImage}>
        &lt;
      </div>

      {/* Săgeată dreapta */}
      <div className="arrow right" onClick={nextImage}>
        &gt;
      </div>
    </div>

      <div className="container">
        <div className="top-box">
          <h2>Chenarul de sus 1</h2>
          <p>Conținut chenar sus 1...</p>
        </div>
        <div className="top-box">
          <h2>Chenarul de sus 2</h2>
          <p>Conținut chenar sus 2...</p>
        </div>
        <div className="bottom-box">
          <h2>Chenarul de jos 1</h2>
          <p>Conținut chenar jos 1...</p>
        </div>
        <div className="bottom-box">
          <h2>Chenarul de jos 2</h2>
          <p>Conținut chenar jos 2...</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
