import React, { useContext, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./styles.css";
import { useGetProducts } from "../../hooks/useGetProducts";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { IProduct } from "../../models/interfaces";

export const CheckoutPage = () => {
  const { getCartItemCount, getTotalCartAmount, checkout } = useContext(ShopContext);
  const totalAmount = getTotalCartAmount();
  const { products } = useGetProducts();
  const navigate = useNavigate();
  const [pdfUrl, setPdfUrl] = useState(null);

  const generatePDF = () => {
    const input = document.getElementById('pdf-content');
    if (!input) return; 

    html2canvas(input).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({ orientation: 'portrait' });
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      const blobPDF = pdf.output('blob');
      const blobUrl = URL.createObjectURL(blobPDF);
      setPdfUrl(blobUrl);
    });
  };

  return (
    <div className="cart" id="pdf-content">
      <h1>Your Cart Items</h1>
      <div>
        {products.map((product: IProduct) => {
          if (getCartItemCount(product._id) !== 0) {
            return <CartItem key={product._id} data={product} />;
          }
          return null;
        })}
      </div>

      {totalAmount > 0 ? (
        <div className="checkout">
          <p>Subtotal: ${totalAmount.toFixed(2)}</p>
          <button onClick={() => navigate("/")}>Continue Shopping</button>
          <button onClick={() => {
            checkout(localStorage.getItem("userID"));
            generatePDF();
          }}>
            Checkout
          </button>
          {pdfUrl && <a href={pdfUrl} download="YourInvoice.pdf">Download Your Invoice</a>}
        </div>
      ) : (
        <h1>Your Shopping Cart is Empty</h1>
      )}
    </div>
  );
};
