import React, { useContext, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";

import "./styles.css";
import { useGetProducts } from "../../hooks/useGetProducts";
import { IProduct } from "../../models/interfaces";

export const CheckoutPage = () => {
  const { getCartItemCount, getTotalCartAmount, checkout } =
    useContext(ShopContext);
  const totalAmount = getTotalCartAmount();
  const { products } = useGetProducts();
  const navigate = useNavigate();
  
  const [showPopup, setShowPopup] = useState(false);

const handleCheckout = () => {
  const userId = localStorage.getItem("userID");
  const larisa = "some value"; // Assuming 'larisa' is a variable that holds some value
  alert("larisa: " + larisa);
};


  const finalizeCheckout = () => {
    setShowPopup(false);

  };

  return (
    <div className="cart">
      <div><h1>Your Cart Items</h1></div>
      <div className="cart-items">
        {products.map((product: IProduct) => {
          if (getCartItemCount(product._id) !== 0) {
            return <CartItem data={product} key={product._id} />;
          }
          return null;
        })}
      </div>
      {totalAmount > 0 ? (
        <div className="checkout">
          <p>Subtotal: ${totalAmount}</p>
          <button onClick={() => navigate("/")}>Continue Shopping</button>
          <button onClick={handleCheckout}>Checkout</button>
        </div>
      ) : (
        <h1>Your Shopping Cart is Empty</h1>
      )}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h2>Order Confirmation</h2>
            <p>Your order has been placed successfully!</p>
            <button onClick={finalizeCheckout}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
};
