import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./styles.css";

export const ConfirmationPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { products, totalAmount, shippingFee } = location.state || {};

  const [grandTotal, setGrandSize] = useState(0);

  useEffect(() => {
    if (!products || products.length === 0 || !totalAmount || !shippingFee) {
      // Dacă datele sunt incomplete sau absente, redirecționează sau afișează un mesaj
      console.error("Invalid state parameters");
      navigate("/cart"); // Sau afișează un mesaj de eroare în loc să redirecționezi
      return;
    }

    setGrandSize(totalAmount + shippingFee);
  }, [totalAmount, shippingFee, products, navigate]);

  // Restul codului rămâne neschimbat
  return (
    <div className="invoice">
      <h1 className="invoice-title">Order Confirmation</h1>
      <div className="invoice-details">
        <h2>Order Summary</h2>
        <ul>
          {products.map((product) => (
            <li key={product._id}>
              {product.name} - ${product.price} x {product.quantity}
            </li>
          ))}
        </ul>
        <p>Subtotal: ${totalAmount.toFixed(2)}</p>
        <p>Shipping Fee: ${shippingFee.toFixed(2)}</p>
        <p><strong>Total: ${grandTotal.toFixed(2)}</strong></p>
      </div>
      <div className="payment-confirmation">
        <h2>Payment Confirmation</h2>
        <p>Your payment has been successfully processed.</p>
        <p>Thank you for your purchase!</p>
      </div>
    </div>
  );
};
