import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./styles.css";

export const ConfirmationPage = () => {
  const location = useLocation();
  const { products, totalAmount } = location.state;

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [shippingFee, setShippingFee] = useState(0);
  const [grandTotal, setGrandTotal] = useState(totalAmount);

  useEffect(() => {
    const randomShippingFee = Math.floor(Math.random() * 20) + 1; // Random shipping fee between 1 and 20
    setShippingFee(randomShippingFee);
    setGrandTotal(totalAmount + randomShippingFee);
  }, [totalAmount]);

  const handleConfirm = (e) => {
    e.preventDefault();
    alert("Order confirmed!");
  };

  return (
    <div className="invoice">
      <h1 className="invoice-title">Order Confirmation</h1>
      <div className="invoice-details">
        <div className="customer-details">
          <h2>Customer Details</h2>
          <form className="confirmation-form" onSubmit={handleConfirm}>
            <label>
              Name:
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </label>
            <label>
              Address:
              <input
                type="text"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                required
              />
            </label>
            <label>
              Phone Number:
              <input
                type="tel"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
            </label>
            <button type="submit">Confirm Order</button>
          </form>
        </div>
        <div className="order-summary">
          <h2>Order Summary</h2>
          <ul>
            {products.map((product) => (
              <li key={product._id}>
                {product.name} - ${product.price} x {product.quantity}
              </li>
            ))}
          </ul>
          <p>Subtotal: ${totalAmount}</p>
          <p>Shipping Fee: ${shippingFee}</p>
          <p><strong>Total: ${grandTotal}</strong></p>
        </div>
      </div>
    </div>
  );
};
