import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./styles.css";

export const ConfirmationPage = () => {
  const location = useLocation();
  const { products, totalAmount, shippingFee } = location.state;

  const [grandTotal, setGrandTotal] = useState(totalAmount + shippingFee);

  useEffect(() => {
    setGrandTotal(totalAmount + shippingFee);
  }, [totalAmount, shippingFee]);

  return (
    <div className="invoice">
      <h1 className="invoice-title">Order Confirmation</h1>
      <div className="invoice-details">
        <h2>Order Summary</h2>
        <ul>
          {products.map((product) => (
            <li key={product._id}>
              {product.name} - ${product.price} x {product.quantity}
            </li>
          ))}
        </ul>
        <p>Subtotal: ${totalAmount}</p>
        <p>Shipping Fee: ${shippingFee}</p>
        <p><strong>Total: ${grandTotal}</strong></p>
      </div>
      <div className="payment-confirmation">
        <h2>Payment Confirmation</h2>
        <p>Your payment has been successfully processed.</p>
        <p>Thank you for your purchase!</p>
      </div>
    </div>
  );
};
