import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { ShopPage } from "./pages/shop";
import { AuthPage } from "./pages/auth";
import { CheckoutPage } from "./pages/checkout";
import { ShopContextProvider } from "./context/shop-context";
import { Navbar } from "./components/navbar";
import { PurchasedItemsPage } from "./pages/purchased-items";

function App() {
  // Definim o variabilă de stare pentru a stoca informația despre autentificare
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <div className="App">
      <Router>
        {" "}
        <ShopContextProvider>
          <Navbar isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
          {/* Utilizăm Routes condiționat pentru a afișa AuthPage doar când utilizatorul este autentificat */}
          <Routes>
            <Route path="/" element={<ShopPage />} />
            {/* Verificăm dacă utilizatorul este autentificat înainte de a afișa AuthPage */}
            {isAuthenticated && <Route path="/auth" element={<AuthPage />} />}
            <Route path="/checkout" element={<CheckoutPage />} />
            <Route path="/purchased-items" element={<PurchasedItemsPage />} />
          </Routes>
        </ShopContextProvider>
      </Router>
    </div>
  );
}

export default App;
