import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import { useContext, useState } from "react";
import { IShopContext, ShopContext } from "../context/shop-context";
import { AuthPage } from "../pages/auth";

export const Navbar = () => {
  const { availableMoney, isAuthenticated, setIsAuthenticated } =
    useContext<IShopContext>(ShopContext);
  const [isAuthPageVisible, setIsAuthPageVisible] = useState(false);

  const logout = () => {
    setIsAuthenticated(false);
    setIsAuthPageVisible(false);  // Ascunde AuthPage după logout
  };

  const login = () => {
    if (!isAuthenticated) { // Afișează AuthPage doar dacă utilizatorul nu este autentificat
      setIsAuthPageVisible(true);
    }
  }

  return (
    <div className="navbar">
      <div className="navbarTitle">
        <h1>iTech</h1>
      </div>
      <div className="navbarLinks">
        {!isAuthenticated && (
          <button onClick={login}>Login</button>  // Schimbat de la Link la button pentru a evita reîncărcarea paginii/routing
        )}
        {isAuthenticated && (
          <>
            <Link to="/shop">Shop</Link>
            <Link to="/purchased-items">Purchases</Link>
            <Link to="/checkout">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Link>
            <button onClick={logout}>Logout</button>  
            <span> ${availableMoney.toFixed(2)} </span>
          </>
        )}
      </div>
      {isAuthPageVisible && <AuthPage />}
    </div>
  );
};
