import "./App.css";
import React, { useState, useContext } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { ShopPage } from "./pages/shop";
import { CheckoutPage } from "./pages/checkout";
import { ShopContextProvider, ShopContext } from "./context/shop-context";
import { Navbar } from "./components/navbar";
import { PurchasedItemsPage } from "./pages/purchased-items";
import Home from "./components/Home";
import { AuthPage } from "./pages/auth";

function App() {
  const { isAuthenticated } = useContext(ShopContext); // Use the context to get the authentication state

  return (
    <div className="App">
      <Router>
        <ShopContextProvider>
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/shop" element={<ShopPage />} />
            <Route path="/checkout" element={<CheckoutPage />} />
            <Route path="/purchased-items" element={<PurchasedItemsPage />} />
            {!isAuthenticated && <Route path="/auth" element={<AuthPage />} />} 
          </Routes>
        </ShopContextProvider>
      </Router>
    </div>
  );
}

export default App;
