import "./App.css";
// Home.tsx
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { ShopContextProvider } from "./context/shop-context";
import Navbar from "./components/navbarLogin"; 
import { AuthPage } from "./pages/auth";
import App from "./App";

function Home() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Funcție pentru a seta starea autentificării
  const handleAuthentication = () => {
    setIsAuthenticated(true);
  };

  return (
    <div className="Home">
      <Router>
        <ShopContextProvider>
          <Navbar onAuthentication={handleAuthentication} />
          <Routes>
            {/* Pagina de autentificare */}
            <Route path="/auth" element={<AuthPage onAuthentication={handleAuthentication} />} />
            {/* Redirecționarea către pagina App.tsx dacă utilizatorul este autentificat */}
            {isAuthenticated ? (
              <Navigate to="/app" />
            ) : (
              <Route path="/app" element={<App />} />
            )}
          </Routes>
        </ShopContextProvider>
      </Router>
    </div>
  );
}

export default Home;
