import React, { useContext, useState } from "react";
import { ShopContext } from "../../context/shop-context";
import { CartItem } from "./cart-item";
import { useNavigate } from "react-router-dom";
import "./styles.css";
import { useGetProducts } from "../../hooks/useGetProducts";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { IProduct } from "../../models/interfaces";

export const CheckoutPage = () => {
  const { getCartItemCount, getTotalCartAmount, checkout, cartItems } = useContext(ShopContext);
  const totalAmount = getTotalCartTheCartAmount();
  const navigate = useNavigate();
  const [pdfUrl, setPdfUrl] = useState(null);

  const generatePDF = () => {
    const input = document.getElementById('pdf-content');
    if (!input) return; 

    html2canvas(input, {
      onclone: (document) => {
        // Hide elements not needed in the PDF
        document.getElementById('download-link').style.visibility = 'hidden';
      }
    }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF({ orientation: 'landscape' });
      let yPos = 10;  // Initial vertical position

      cartItems.forEach((item, index) => {
        const imgProps = pdf.getImageProperties(imgData);
        const pdfWidth = pdf.internal.pageSize.getWidth() - 20;  // margin
        const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
        if (index > 0) yPos += pdfHeight + 10;  // add margin between images

        pdf.addImage(imgData, 'PNG', 10, yPos, pdfWidth, pdfHeight);
        pdf.text(item.productName + " - $" + item.price.toFixed(2), 15, yPos + pdfHeight + 10);
      });

      const blobPDF = pdf.output('blob');
      const blobUrl = URL.createObjectURL(blobPDF);
      setPdfUrl(blobUrl);
    });
  };

  return (
    <div className="cart" id="pdf-content">
      <h1>Your Cart Items</h1>
      {cartItems.map((item) => (
        <CartItem key={item._id} data={item} />
      ))}

      {totalAmount > 0 ? (
        <div className="checkout">
          <p>Subtotal: ${totalAmount.toFixed(2)}</p>
          <button onClick={() => navigate("/")}>Continue Shopping</button>
          <button onClick={() => {
            checkout(localStorage.getItem("userID"));
            generatePDF();
          }}>
            Checkout
          </button>
          {pdfUrl && <a href={pdfUrl} download="YourInvoice.pdf" id="download-link">Download Your Invoice</a>}
        </div>
      ) : (
        <h1>Your Shopping Cart is Empty</h1>
      )}
    </div>
  );
};
