import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import { useContext, useState } from "react"; // Importăm useState
import { IShopContext, ShopContext } from "../context/shop-context";
import { AuthPage } from "../pages/auth"; // Importăm direct componenta AuthPage

export const Navbar = () => {
  const navigate = useNavigate(); // Hook pentru navigare programatică
  const { availableMoney, isAuthenticated, setIsAuthenticated } =  useContext<IShopContext>(ShopContext);
  const [showAuthPage, setShowAuthPage] = useState(false); // Starea pentru afișarea sau ascunderea paginii de autentificare
  
  const logout = () => {
    setIsAuthenticated(false);
    navigate("/auth"); // Navigare către pagina de autentificare
  };

  const login = () => {
    // Afișăm pagina de autentificare doar dacă utilizatorul nu este autentificat
    if (!isAuthenticated) {
      setShowAuthPage(true);
    }
  };
  
  return (
    <div className="navbar">
      <div className="navbarTitle">
        <h1>PedroTech Shop</h1>
      </div>
      <div className="navbarLinks">
        {!isAuthenticated && (
          <>
            <button onClick={login}>Login</button>
          </>  
        )}

        {isAuthenticated && (
          <>
            <Link to="/">Shop</Link>
            <Link to="/purchased-items">Purchases</Link>
            <Link to="/checkout">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Link>
            <button onClick={logout}>Logout</button>
            <span> ${availableMoney.toFixed(2)} </span>
          </>
        )}

        {/* Afișăm pagina de autentificare doar dacă showAuthPage este true */}
        {showAuthPage && <AuthPage />}
      </div>
    </div>
  );
};
