import "./App.css";
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./components/Home";
import { Navbar } from "./components/Navbar";
import { AuthPage } from "./pages/AuthPage";
import { ShopPage } from "./pages/ShopPage";
import { CheckoutPage } from "./pages/CheckoutPage";
import { PurchasedItemsPage } from "./pages/PurchasedItemsPage";
import { ShopContextProvider } from "./context/ShopContext";

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <div className="App">
      <Router>
        <ShopContextProvider>
          <Navbar isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/auth" element={!isAuthenticated ? <AuthPage setIsAuthenticated={setIsAuthenticated} /> : <Navigate replace to="/" />} />
            <Route path="/shop" element={isAuthenticated ? <ShopPage /> : <Navigate replace to="/auth" />} />
            <Route path="/checkout" element={isAuthenticated ? <CheckoutPage /> : <Navigate replace to="/auth" />} />
            <Route path="/purchased-items" element={isAuthenticated ? <PurchasedItemsPage /> : <Navigate replace to="/auth" />} />
          </Routes>
        </ShopContextProvider>
      </Router>
    </div>
  );
};

export default App;
