// Home.tsx
import React, { useState } from "react";
import App from "./App"; // Componenta care reprezintă conținutul App
import { Navbar } from "./components/navbar"; // Importă Navbar

function Home() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="Home">
      <Navbar /> {/* Afișează Navbar pe pagina principală */}
      {isLoggedIn ? (
        <App /> // Afișează conținutul lui App doar când utilizatorul este logat
      ) : (
        <div>
          <h1>Bine ai venit pe pagina principală!</h1>
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
}

export default Home;
