import React, { useState, useContext } from "react";
import { ShopContext } from "../../context/shop-context";
import { useNavigate } from "react-router-dom";
import "./styless.css";

export const AddMoneyPage = () => {
  const [amount, setAmount] = useState('');
  const navigate = useNavigate();
  const { addMoneyToBalance, fetchAvailableMoney } = useContext(ShopContext); 

  const handleSubmit = async (event) => {
    event.preventDefault();
    const amountNumber = parseFloat(amount);
    if (!isNaN(amountNumber) && amountNumber > 0) {
      await addMoneyToBalance(amountNumber);
      await fetchAvailableMoney();
      setAmount('');
      navigate('/'); 
    } else {
      alert("Please enter a valid amount");
    }
  };

  return (
    <div className="addMoney">
      <h1>Add your money</h1>
      <div className="auth-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="amount">Amount:</label>
            <input
              type="number"
              id="amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Enter amount"
            />
          </div>
          <button type="submit" className="b">Add Money</button>
        </form>
      </div>
    </div>
  );
};
