import { ReactNode, createContext, useEffect, useState } from "react";
import { useGetProducts } from "../hooks/useGetProducts";
import { IProduct } from "../models/interfaces";
import axios from "axios";
import { ProductErrors } from "../models/errors";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { useGetToken } from "../hooks/useGetToken";

export interface IShopContext {
  getCartItemCount: (itemId: string) => number;
  addToCart: (itemId: string) => void;
  updateCartItemCount: (newAmount: number, itemId: string) => void;
  getTotalCartAmount: () => number;
  removeFromCart: (itemId: string) => void;
  checkout: () => void;
  availableMoney: number;
  fetchAvailableMoney: () => void;
  purchasedItems: IProduct[];
  isAuthenticated: boolean;
  setIsAuthenticated: (isAuthenticated: boolean) => void;
  isAdmin: boolean;
  isSupplier: boolean;
  cartItems: Record<string, number>;
}

export const ShopContext = createContext<IShopContext | null>(null);

export const ShopContextProvider = (props) => {
  const [cookies, setCookies] = useCookies(["access_token"]);
  const [cartItems, setCartItems] = useState<Record<string, number>>({});
  const [availableMoney, setAvailableMoney] = useState<number>(0);
  const [purchasedItems, setPurchaseItems] = useState<IProduct[]>([]);
  const [isAuthenticated, setIsAuthenticated] = useState<boolean>(cookies.access_token !== undefined);
  const [isAdmin, setIsAdmin] = useState<boolean>(false);
  const [isSupplier, setIsSupplier] = useState<boolean>(false);

  const { products, fetchProducts } = useGetProducts();
  const { headers } = useGetToken();
  const navigate = useNavigate();

  useEffect(() => {
    const userID = localStorage.getItem("userID");
    setIsAdmin(userID === "admin");
    setIsSupplier(userID === "supplier");
  }, [isAuthenticated]);

  const fetchAvailableMoney = async () => {
    const res = await axios.get(
      `http://localhost:3001/auth/available-money/${localStorage.getItem("userID")}`,
      { headers }
    );
    setAvailableMoney(res.data.availableMoney);
  };

  const fetchPurchasedItems = async () => {
    const res = await axios.get(
      `http://localhost:3001/products/purchased-items/${localStorage.getItem("userID")}`,
      { headers }
    );
    setPurchaseItems(res.data.purchasedItems);
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchAvailableMoney();
      fetchPurchasedItems();
    }
  }, [isAuthenticated]);

  useEffect(() => {
    if (!isAuthenticated) {
      localStorage.clear();
      setCookies("access_token", null);
    }
  }, [isAuthenticated]);

  const getCartItemCount = (itemId: string): number => {
    return cartItems[itemId] || 0;
  };

  const getTotalCartAmount = () => {
    if (products.length === 0) return 0;

    let totalAmount = 0;
    for (const itemId in cartItems) {
      if (cartItems[itemId] > 0) {
        const itemInfo = products.find(product => product._id === itemId);
        if (itemInfo) {
          totalAmount += cartItems[itemId] * itemInfo.price;
        }
      }
    }
    return Number(totalAmount.toFixed(2));
  };

  const addToCart = (itemId: string) => {
    setCartItems(prev => ({ ...prev, [itemId]: (prev[itemId] || 0) + 1 }));
  };

  const removeFromCart = (itemId: string) => {
    setCartItems(prev => {
      const newCartItems = { ...prev };
      if (newCartItems[itemId] > 1) {
        newCartItems[itemId] -= 1;
      } else {
        delete newCartItems[itemId];
      }
      return newCartItems;
    });
  };

  const updateCartItemCount = (newAmount: number, itemId: string) => {
    setCartItems(prev => ({ ...prev, [itemId]: newAmount }));
  };

  const checkout = async () => {
    const body = { customerID: localStorage.getItem("userID"), cartItems };
    try {
      const res = await axios.post("http://localhost:3001/products/checkout", body, { headers });
      setPurchaseItems(res.data.purchasedItems);
      fetchAvailableMoney();
      fetchProducts();
      navigate("/");
    } catch (err) {
      let errorMessage = "Something went wrong";
      if (err.response && err.response.data) {
        switch (err.response.data.type) {
          case ProductErrors.NO_PRODUCT_FOUND:
            errorMessage = "No product found";
            break;
          case ProductErrors.NO_AVAILABLE_MONEY:
            errorMessage = "Not enough money";
            break;
          case ProductErrors.NOT_ENOUGH_STOCK:
            errorMessage = "Not enough stock";
            break;
        }
      }
      alert("ERROR: " + errorMessage);
    }
  };

  const contextValue: IShopContext = {
    getCartItemCount,
    addToCart,
    updateCartItemCount,
    removeFromCart,
    getTotalCartAmount,
    checkout,
    availableMoney,
    fetchAvailableMoney,
    purchasedItems,
    isAuthenticated,
    setIsAuthenticated,
    isAdmin,
    isSupplier,
    cartItems
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};
